import React from 'react';
import {Card, CardActions, CardMedia, Button, Typography, CardContent} from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment'; 
import {useDispatch} from 'react-redux';

import {deletePost, likePost} from '../../../actions/Posts';
import useStyles from './styles';
const Post = ({post, setCurrentId }) => {
    const classes=useStyles();
    const dispatch=useDispatch();

    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={post.selectedFile} title={post.title}>
                <div className={classes.overlay}>
                    <Typography variant="h6" >{post.creator}</Typography>
                    <Typography variant="h6" >{moment(post.createdAt).fromNow()}</Typography>
                </div>
                <div className="classes overlay">
                    <Button style={{color:'white'}} size="small" onClick={()=> setCurrentId(post._id)} >
                        <MoreHorizIcon />
                    </Button>
                </div>
                <div className="clases details">
                    <Typography variant="body2" color="textSecondary"  >{post.tags.map((tag)=> `#${tag} `)}</Typography>
                </div>

                <Typography className={classes.title} variant="h5" gutterBottom >{post.title}</Typography>

                <CardContent>
                    <Typography  variant="body2" color="textSecondry" component="p" >{post.message}</Typography>
                </CardContent>

                <CardActions className={classes.cardActions} >
                    <Button size="small" color="primary" onClick={()=>dispatch(likePost(post._id)) }  >
                        <ThumbUpAltIcon fontSize="small" />
                        &nbsp; Like &nbsp;
                        {post.LikeCount}
                    </Button>
                    <Button size="small" color="primary" onClick={()=>dispatch(deletePost(post._id)) }  >
                        <DeleteIcon fontSize="small" />
                        Delete
                    </Button>
                </CardActions>

                
            </CardMedia>
        </Card>
    )
}

export default Post
